import tkinter as tk
import threading
import time
import random

click_times = 0

def dow():
    click_times+=1
    if click_times > 30:
        return
    window = tk.Tk()
    width = window.winfo_screenwidth()
    height = window.winfo_screenheight()
    a = random.randrange(0,width)
    b = random.randrange(0,height)
    window.title("最爱初见你")
    window.geometry("200x50"+"+"+str(a)+"+"+str(b))
    tk.Label(window,text='女人！喜欢你！',bg='pink',fg='white',font=('楷体',14,),width=200,height=5).pack()
    window.mainloop()
    threads=[]
    for i in range(10):
        t = threading.Thread(target=dow)
        threads.append(t)
        time.sleep(0.1)
        threads[i].start()
    

if __name__ == "__main__":
    
    dow()
